con:config.script:
import groovy.json.JsonSlurper

def response =testRunner.testCase.testSteps["AddingNewCustomer"].testRequest.response.contentAsString

def json = new JsonSlurper().parseText(response)

def customerid=json.data.id.toString()

testRunner.testCase.getTestStepByName("AddingProperty").setPropertyValue("CustomerId",customerid)

